import { of } from 'rxjs'; // Асинхронный валидатор

export function matchFieldsAsyncValidator(field1, field2) {
  return control => {
    const value1 = control.get(field1).value;
    const value2 = control.get(field2).value;

    if (value1 !== value2) {
      // Возвращаем ошибку, если значения не совпадают
      return of({
        matchFields: true
      });
    } // Если значения совпадают, возвращаем null


    return of(null);
  };
}
