import { Component, ElementRef, OnInit, ViewChild, inject } from '@angular/core';
import { FormBuilder, FormGroup, FormsModule, Validators, ValidatorFn, AbstractControl } from '@angular/forms';
import { Router } from '@angular/router';
import { matchFieldsAsyncValidator } from './matchFieldsValidator';
import { AuthService } from '../auth.service';
import { HttpClientModule } from '@angular/common/http';

@Component({
  selector: 'app-loginpage',
  templateUrl: './loginpage.component.html',
  styleUrls: ['./loginpage.component.css'],
})
export class LoginpageComponent implements OnInit {
  loginForm!: FormGroup;
  registerForm!: FormGroup;
  fb = inject(FormBuilder);
  router = inject(Router);
  authService = inject(AuthService);

  errorMsg: string = 'error';
  registerErrorMsg: string = 'error';






  ngOnInit(): void {
    this.loginForm = this.fb.group(
      {
        login: ['', [Validators.required, Validators.minLength(6)]],
        password: ['', [Validators.required, Validators.minLength(8)]]
      }
    );
    this.registerForm = this.fb.group(
      {
        login: ['', [Validators.required, Validators.minLength(6)]],
        password: ['', [Validators.required, Validators.minLength(8)]],
        confirmPassword: ['', [Validators.required, Validators.minLength(8)]]
      },
      {
        asyncValidators: [matchFieldsAsyncValidator('password', 'confirmPassword')],
      }
    );
  }

  isLoginFormVisible: boolean = true;
  isRegisterFormVisible: boolean = false;
  isFirstButtonSelected: boolean = true;
  isSecondButtonSelected: boolean = false;
  isLoginErrorVisible: boolean = false;
  isRegisterErrorVisible: boolean = false;
  isRegisterSuccessful: boolean = false;


  toogleForm(formId:string){
    if(formId === 'loginForm'){
      this.isLoginFormVisible = true;
      this.isRegisterFormVisible = false;
      this.isFirstButtonSelected = true;
      this.isSecondButtonSelected = false;
    }
    else if(formId === 'registerForm'){
      this.isLoginFormVisible = false;
      this.isRegisterFormVisible = true;
      this.isFirstButtonSelected = false;
      this.isSecondButtonSelected = true;
      this.isLoginErrorVisible = false;
    }
  }

  login(): void {
    this.isLoginErrorVisible = false;
    const login = this.loginForm.get('login')?.value;
    const password = this.loginForm.get('password')?.value;
    this.authService.login(login, password).subscribe(
      (response) => {
        this.authService.setToken(response.token);
        this.router.navigate(['']);
      },
      (error) => {
        this.isLoginErrorVisible = true;
        this.errorMsg = error.error;
      }
    )
  }
  register(): void {
    this.isRegisterErrorVisible = false;
    const login = this.registerForm.get('login')?.value;
    const password = this.registerForm.get('password')?.value;
    this.authService.register(login, password).subscribe(
      (response) => {
        console.log('register succsesful');
        this.toogleForm('loginForm');
        this.isRegisterSuccessful = true;
      },
      (error) => {
        this.isRegisterErrorVisible = true;
        this.registerErrorMsg = error.error;
      }
    )
  }
}
<div class="loginpage-container">
    <div class="login-content">
      <div class="button-menu">
        <button [ngClass]="{ 'SelectedBtn': isFirstButtonSelected}" id="loginBtn" class="Btn" (click)="toogleForm('loginForm')">SIGN IN</button>
        <button [ngClass]="{ 'SelectedBtn': isSecondButtonSelected}" id="registerBtn" class="Btn" (click)="toogleForm('registerForm')">SIGN UP</button>
      </div>
      <div class="forms-container">
        <form [formGroup]="loginForm" *ngIf="isLoginFormVisible" id="loginForm" class="loginForm">
          <p class="psign">SIGN IN</p>
          <div class="inputs-container">
            <label class="label">Login</label>
            <input formControlName="login" type="text" class="logininput" placeholder="Input login here...">
            <div class="errormsglogin" *ngIf="loginForm.get('login')?.hasError('minlength')">
              Minimal length: 6
            </div>
            <label class="label">Password</label>
            <input formControlName="password" type="password" class="passwordinput" placeholder="Input password here...">
          </div>
          <button [disabled]="loginForm.invalid" type="submit" class="inputBtn" (click)="login()">
            Sign in
          </button>
        </form>
        <form [formGroup]="registerForm" *ngIf="isRegisterFormVisible" id="loginForm" class="loginForm">
          <p class="psigp">REGISTER</p>
          <div class="inputs-container">
            <label class="label">Login</label>
            <input formControlName="login" type="text" class="logininput" placeholder="Input login here...">
            <label class="label">Password</label>
            <input formControlName="password" type="password" class="passwordinput" placeholder="Input password here...">
            <label class="label">Repeat the password</label>
            <input formControlName="confirmPassword" type="password" class="passwordinput" placeholder="Repeat the password here...">
          </div>
          <button [disabled]="registerForm.invalid" type="submit" class="regBtn" (click)="register()">
            Register
          </button>
        </form>
      </div>
      <div *ngIf="isLoginErrorVisible" class="errorLoginDiv">
        <p class="errorMsg">
          {{errorMsg}}
        </p>
      </div>
      <div *ngIf="isRegisterErrorVisible" class="errorLoginDiv">
        <p class="errorMsg">
          {{registerErrorMsg}}
        </p>
      </div>
      <div *ngIf="isRegisterSuccessful" class="successfulRegisterDiv">
        <p class="successMsg">
          Registration successful
        </p>
      </div>
    </div>
  </div>
